mapper.update:
UPDATE torrent_info_tbl SET

    hash_code=#{hashCode}


    ,status=#{status}

WHERE post_url=#{url}

UPDATE torrent_info_tbl SET
status=#{status}

WHERE post_url IN

  #{url}

mapper.select:
SELECT post_url url,hash_code hashCode,title title,status status,is_download isDownload FROM torrent_info_tbl
WHERE 1=1

    AND status=#{status}

limit 0,

    #{count}
